SELECT ROWNUM ,trim(SCN_ID) as SCN_ID ,trim(VIDEO_ID) as VIDEO_ID ,(select titles from video where video_id=A.VIDEO_ID) as VIDEO_NM ,SCN_START_CD ,SCN_END_CD ,UPD_DTIME ,EDITOR ,EVENT_LCLS ,EVENT_SCLS ,EVENT_NM ,trim(EVENT_PRD) as EVENT_PRD ,trim(DOM_ABR) as DOM_ABR ,trim(CNTRY_CD) as CNTRY_CD ,EVENT_PLACE ,REAL_PLACE ,LATITUDE ,LONGITUDE ,CELEBRITY1 ,CELEBRITY2 ,CELEBRITY3 ,DESCRIPTION ,NARRATION ,SUBTITLES ,SUMMARY ,KEYWORD ,NOTE FROM SCENE_MANAGE A
SCN_ID = ( select min(SCN_ID) from SCENE_MANAGE where SCN_ID > #{scnId} ) AND VIDEO_ID = #{vdoId}
SCN_ID = ( select max(SCN_ID) from SCENE_MANAGE where SCN_ID < #{scnId} ) AND VIDEO_ID = #{vdoId}
SCN_ID = #{scnId} AND VIDEO_ID = #{vdoId}
