mapper.select:
select * from ( SELECT row_number() over (order by
${sortMap.field} ${sortMap.direction}
to_number(substr(scnStartCd, 0, instr(scnStartCd, ':')-1)) asc, to_number(substr(scnStartCd, instr(scnStartCd, ':')+1)) asc
) as recid, X.* from ( select trim(SCN_ID) as scnId , trim(VIDEO_ID) as vdoId , (select MUL_TITLE from MULTI_NEW where MUL_ID=A.VIDEO_ID) as vdoNm , SCN_START_CD as scnStartCd , SCN_END_CD as scnEndCd , UPD_DTIME as updDtime , EDITOR as editor , EVENT_LCLS as eventLClasCd , EVENT_SCLS as eventSClasCd , EVENT_NM as eventNm , trim(EVENT_PRD) as eventPrd , trim(DOM_ABR) as domAbr , trim(CNTRY_CD) as cntryCd , EVENT_PLACE as eventPlace , REAL_PLACE as realPlace , LATITUDE as latitude , LONGITUDE as longitude , CELEBRITY1 as celebrity1 , CELEBRITY2 as celebrity2 , CELEBRITY3 as celebrity3 , DESCRIPTION as description , NARRATION as narration , SUBTITLES as subtitles , SUMMARY as summary , KEYWORD as keyword , NOTE as note FROM SCENE_MANAGE A WHERE VIDEO_ID = #{vdoId} ) X )

SELECT ROWNUM as recid ,trim(SCN_ID) as scnId ,trim(VIDEO_ID) as vdoId ,(select MUL_TITLE from MULTI_NEW where MUL_ID=A.VIDEO_ID) as vdoNm ,SCN_START_CD as scnStartCd ,SCN_END_CD as scnEndCd ,UPD_DTIME as updDtime ,EDITOR as editor ,EVENT_LCLS as eventLClasCd ,EVENT_SCLS as eventSClasCd ,EVENT_NM as eventNm ,trim(EVENT_PRD) as eventPrd ,trim(DOM_ABR) as domAbr ,trim(CNTRY_CD) as cntryCd ,EVENT_PLACE as eventPlace ,REAL_PLACE as realPlace ,LATITUDE as latitude ,LONGITUDE as longitude ,CELEBRITY1 as celebrity1 ,CELEBRITY2 as celebrity2 ,CELEBRITY3 as celebrity3 ,DESCRIPTION as description ,NARRATION as narration ,SUBTITLES as subtitles ,SUMMARY as summary ,KEYWORD as keyword ,NOTE as note FROM SCENE_MANAGE A
SCN_ID = ( select min(SCN_ID) from SCENE_MANAGE where SCN_ID > #{scnId} ) AND VIDEO_ID = #{vdoId}
SCN_ID = ( select max(SCN_ID) from SCENE_MANAGE where SCN_ID < #{scnId} ) AND VIDEO_ID = #{vdoId}
SCN_ID = #{scnId} AND VIDEO_ID = #{vdoId}

mapper.delete:
delete from SCENE_MANAGE
 where SCN_ID = #{scnId}
   and VIDEO_ID = #{vdoId}
